import { useState } from "react";
import * as React from "react";
import { Search, Plus, Tag, Calendar, Edit3, Trash2, Filter, Star } from "lucide-react";
import { showToast } from "@/components/ui/toast";
import { useConfirmDialog } from "@/components/ui/ConfirmDialog";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";

interface Note {
  id: string;
  title: string;
  content: string;
  tags: string[];
  createdAt: Date;
  updatedAt: Date;
  category: 'strategy' | 'reflection' | 'plan' | 'idea';
  starred: boolean;
}

export function NotesPanel() {
  const queryClient = useQueryClient();
  const { showConfirm, confirmDialog } = useConfirmDialog();

  // Fetch notes from database
  const { data: notes = [], isLoading } = useQuery({
    queryKey: ['/api/notes'],
    queryFn: async () => {
      const response = await fetch('/api/notes');
      if (!response.ok) throw new Error('Failed to fetch notes');
      return response.json();
    }
  });

  // Create note mutation
  const createNoteMutation = useMutation({
    mutationFn: async (noteData: any) => {
      const response = await fetch('/api/notes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(noteData)
      });
      if (!response.ok) throw new Error('Failed to create note');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notes'] });
    }
  });

  // Update note mutation
  const updateNoteMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/notes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Failed to update note');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notes'] });
    }
  });

  // Delete note mutation
  const deleteNoteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/notes/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Failed to delete note');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notes'] });
    }
  });

  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [isCreating, setIsCreating] = useState(false);
  const [editingNote, setEditingNote] = useState<string | null>(null);
  const [newNote, setNewNote] = useState({
    title: '',
    content: '',
    category: 'plan' as Note['category'],
    tags: [] as string[]
  });

  const categories = [
    { id: 'all', name: 'All Notes', icon: 'ðŸ“‹', color: 'text-gray-400' },
    { id: 'strategy', name: 'Strategies', icon: 'ðŸŽ¯', color: 'text-blue-400' },
    { id: 'reflection', name: 'Reflections', icon: 'ðŸ”', color: 'text-purple-400' },
    { id: 'plan', name: 'Plans', icon: 'ðŸ“', color: 'text-green-400' },
    { id: 'idea', name: 'Ideas', icon: 'ðŸ’¡', color: 'text-amber-400' }
  ];

  const filteredNotes = notes.filter(note => {
    const matchesSearch = note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = selectedCategory === 'all' || note.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const handleCreateNote = () => {
    if (!newNote.title.trim()) {
      showToast({
        type: 'warning',
        title: 'Title Required',
        message: 'Please enter a title for your archive entry'
      });
      return;
    }

    createNoteMutation.mutate({
      title: newNote.title,
      content: newNote.content,
      category: newNote.category,
      tags: newNote.tags,
      starred: false
    }, {
      onSuccess: (note) => {
        setNewNote({ title: '', content: '', category: 'plan', tags: [] });
        setIsCreating(false);

        showToast({
          type: 'success',
          title: 'Archive Entry Created!',
          message: `"${note.title}" has been added to your Hunter's Archive`
        });
      },
      onError: () => {
        showToast({
          type: 'error',
          title: 'Failed to Create Note',
          message: 'Please try again'
        });
      }
    });
  };

  const handleDeleteNote = (noteId: string) => {
    const note = notes.find(n => n.id === noteId);
    if (!note) return;

    showConfirm(
      'Delete Archive Entry',
      `Are you sure you want to delete "${note.title}"? This action cannot be undone.`,
      () => {
        deleteNoteMutation.mutate(noteId, {
          onSuccess: () => {
            showToast({
              type: 'success',
              title: 'Entry Deleted',
              message: `"${note.title}" has been removed from your archive`
            });
          },
          onError: () => {
            showToast({
              type: 'error',
              title: 'Failed to Delete Note',
              message: 'Please try again'
            });
          }
        });
      },
      'danger'
    );
  };

  const handleToggleStar = (noteId: string) => {
    const note = notes.find(n => n.id === noteId);
    if (!note) return;

    updateNoteMutation.mutate({
      id: noteId,
      data: { starred: !note.starred }
    }, {
      onSuccess: () => {
        showToast({
          type: 'success',
          title: !note.starred ? 'Entry Starred!' : 'Star Removed',
          message: `"${note.title}" ${!note.starred ? 'added to' : 'removed from'} starred entries`
        });
      },
      onError: () => {
        showToast({
          type: 'error',
          title: 'Failed to Update Note',
          message: 'Please try again'
        });
      }
    });
  };

  const handleAddTag = (tag: string) => {
    if (tag.trim() && !newNote.tags.includes(tag.trim())) {
      setNewNote(prev => ({
        ...prev,
        tags: [...prev.tags, tag.trim()]
      }));
    }
  };

  const handleRemoveTag = (tagToRemove: string) => {
    setNewNote(prev => ({
      ...prev,
      tags: prev.tags.filter(tag => tag !== tagToRemove)
    }));
  };

  return (
    <div className="w-full max-w-none space-y-6">
      {/* Header */}
      <div className="hunter-status-window p-6">
        <div className="flex flex-col lg:flex-row lg:items-center justify-between mb-6">
          <div className="mb-4 lg:mb-0">
            <h2 className="text-2xl font-bold text-white font-['Orbitron'] mb-2">
              <span className="bg-gradient-to-r from-green-400 to-emerald-400 bg-clip-text text-transparent">
                HUNTER'S ARCHIVE
              </span>
            </h2>
            <p className="text-gray-400">Document your productivity journey</p>
          </div>
          <button
            onClick={() => setIsCreating(true)}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition-colors shadow-lg flex items-center"
          >
            <Plus className="w-5 h-5 mr-2" />
            New Entry
          </button>
        </div>

        {/* Search and Filters */}
        <div className="flex flex-col md:flex-row gap-4 mb-6">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search notes, tags, or content..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 bg-gray-800 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:border-cyan-400 focus:outline-none"
            />
          </div>
          <div className="flex space-x-2 overflow-x-auto">
            {categories.map(category => (
              <button
                key={category.id}
                onClick={() => setSelectedCategory(category.id)}
                className={`flex items-center space-x-2 px-4 py-3 rounded-lg whitespace-nowrap transition-all duration-200 ${
                  selectedCategory === category.id
                    ? 'bg-cyan-500/20 border border-cyan-400 text-cyan-400'
                    : 'bg-gray-800 border border-gray-600 text-gray-300 hover:bg-gray-700'
                }`}
              >
                <span>{category.icon}</span>
                <span className="font-medium">{category.name}</span>
              </button>
            ))}
          </div>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="mystical-card p-4 text-center">
            <div className="text-2xl font-bold text-cyan-400">{notes.length}</div>
            <div className="text-gray-400 text-sm">Total Notes</div>
          </div>
          <div className="mystical-card p-4 text-center">
            <div className="text-2xl font-bold text-amber-400">{notes.filter(n => n.starred).length}</div>
            <div className="text-gray-400 text-sm">Starred</div>
          </div>
          <div className="mystical-card p-4 text-center">
            <div className="text-2xl font-bold text-green-400">
              {Array.from(new Set(notes.flatMap(n => n.tags))).length}
            </div>
            <div className="text-gray-400 text-sm">Tags</div>
          </div>
          <div className="mystical-card p-4 text-center">
            <div className="text-2xl font-bold text-purple-400">
              {notes.filter(n => n.createdAt > new Date(Date.now() - 7*24*60*60*1000)).length}
            </div>
            <div className="text-gray-400 text-sm">This Week</div>
          </div>
        </div>
      </div>

      {/* Create New Note Modal */}
      {isCreating && (
        <div className="hunter-status-window p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-bold text-white font-['Orbitron']">New Archive Entry</h3>
            <button
              onClick={() => setIsCreating(false)}
              className="text-gray-400 hover:text-white"
            >
              âœ•
            </button>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Title</label>
              <input
                type="text"
                value={newNote.title}
                onChange={(e) => setNewNote(prev => ({ ...prev, title: e.target.value }))}
                placeholder="Enter note title..."
                className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-lg text-white focus:border-cyan-400 focus:outline-none"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Category</label>
              <select
                value={newNote.category}
                onChange={(e) => setNewNote(prev => ({ ...prev, category: e.target.value as Note['category'] }))}
                className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-lg text-white focus:border-cyan-400 focus:outline-none"
              >
                {categories.slice(1).map(category => (
                  <option key={category.id} value={category.id}>
                    {category.icon} {category.name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Content</label>
              <textarea
                value={newNote.content}
                onChange={(e) => setNewNote(prev => ({ ...prev, content: e.target.value }))}
                placeholder="Write your thoughts, strategies, or reflections..."
                rows={6}
                className="w-full px-4 py-3 bg-gray-800 border border-gray-600 rounded-lg text-white focus:border-cyan-400 focus:outline-none resize-none"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">Tags</label>
              <div className="flex flex-wrap gap-2 mb-2">
                {newNote.tags.map(tag => (
                  <span
                    key={tag}
                    className="inline-flex items-center px-3 py-1 bg-blue-500/20 text-blue-400 rounded-full text-sm"
                  >
                    {tag}
                    <button
                      onClick={() => handleRemoveTag(tag)}
                      type="button"
                      className="ml-2 text-blue-300 hover:text-white"
                    >
                      âœ•
                    </button>
                  </span>
                ))}
              </div>
              <input
                type="text"
                placeholder="Add tags (press Enter)..."
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    handleAddTag(e.currentTarget.value);
                    e.currentTarget.value = '';
                  }
                }}
                className="w-full px-4 py-2 bg-gray-800 border border-gray-600 rounded-lg text-white focus:border-cyan-400 focus:outline-none text-sm"
              />
            </div>

            <div className="flex space-x-4">
              <button
                onClick={handleCreateNote}
                className="flex-1 px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-semibold transition-colors shadow-lg"
              >
                Save Entry
              </button>
              <button
                onClick={() => setIsCreating(false)}
                className="px-6 py-3 bg-gray-700 hover:bg-gray-600 text-white rounded-lg font-semibold transition-colors"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Notes List */}
      <div className="space-y-4">
        {filteredNotes.length === 0 ? (
          <div className="hunter-status-window p-8 text-center">
            <div className="w-20 h-20 bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-4">
              <Edit3 className="w-10 h-10 text-gray-500" />
            </div>
            <h3 className="text-xl font-bold text-white mb-2">No Notes Found</h3>
            <p className="text-gray-400 mb-6">
              {searchTerm || selectedCategory !== 'all' 
                ? 'Try adjusting your search or filter criteria'
                : 'Start documenting your productivity journey by creating your first note'
              }
            </p>
            {!searchTerm && selectedCategory === 'all' && (
              <button
                onClick={() => setIsCreating(true)}
                className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg font-medium transition-colors shadow-lg flex items-center"
              >
                <Plus className="w-5 h-5 mr-2" />
                Create First Note
              </button>
            )}
          </div>
        ) : (
          filteredNotes.map(note => (
            <div key={note.id} className="mystical-card p-6 group">
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <div className="flex items-center space-x-3 mb-2">
                    <h3 className="text-lg font-bold text-white">{note.title}</h3>
                    <span className={`text-sm px-2 py-1 rounded-full ${
                      categories.find(c => c.id === note.category)?.color || 'text-gray-400'
                    } bg-gray-800`}>
                      {categories.find(c => c.id === note.category)?.icon} {note.category}
                    </span>
                    {note.starred && <Star className="w-4 h-4 text-amber-400 fill-current" />}
                  </div>
                  <p className="text-gray-300 mb-4 leading-relaxed">{note.content}</p>

                  {/* Tags */}
                  {note.tags.length > 0 && (
                    <div className="flex flex-wrap gap-2 mb-4">
                      {note.tags.map(tag => (
                        <span
                          key={tag}
                          className="inline-flex items-center px-2 py-1 bg-blue-500/10 text-blue-400 rounded text-xs"
                        >
                          <Tag className="w-3 h-3 mr-1" />
                          {tag}
                        </span>
                      ))}
                    </div>
                  )}

                  {/* Date */}
                  <div className="flex items-center text-gray-500 text-sm">
                    <Calendar className="w-4 h-4 mr-1" />
                    Created {note.createdAt.toLocaleDateString()}
                    {note.updatedAt > note.createdAt && (
                      <span className="ml-2">â€¢ Updated {note.updatedAt.toLocaleDateString()}</span>
                    )}
                  </div>
                </div>

                {/* Actions */}
                <div className="flex items-center space-x-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    onClick={() => handleToggleStar(note.id)}
                    className={`p-2 rounded-lg transition-colors ${
                      note.starred ? 'text-amber-400 hover:text-amber-300' : 'text-gray-400 hover:text-amber-400'
                    }`}
                  >
                    <Star className={`w-4 h-4 ${note.starred ? 'fill-current' : ''}`} />
                  </button>
                  <button
                    onClick={() => setEditingNote(note.id)}
                    className="p-2 text-gray-400 hover:text-cyan-400 rounded-lg transition-colors"
                  >
                    <Edit3 className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleDeleteNote(note.id)}
                    className="p-2 text-gray-400 hover:text-red-400 rounded-lg transition-colors"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
      {confirmDialog}
    </div>
  );
}

client/src/components/features/Notificat.content:
import { useState, useEffect } from "react";
import { Bell, Settings, X, Check } from "lucide-react";
import { showToast } from "@/components/ui/toast";

interface NotificationSystemProps {
  isOpen: boolean;
  onClose: () => void;
}

interface Reminder {
  id: string;
  title: string;
  time: string;
  enabled: boolean;
  weekly?: boolean;
}

export function NotificationSystem({ isOpen, onClose }: NotificationSystemProps) {
  const [permission, setPermission] = useState<NotificationPermission>('default');
  const [reminders, setReminders] = useState<Reminder[]>(() => {
    const saved = localStorage.getItem('notification-reminders');
    return saved ? JSON.parse(saved) : [
      { id: '1', title: 'Daily Quest Reminder', time: '09:00', enabled: false },
      { id: '2', title: 'Evening Review', time: '18:00', enabled: false },
      { id: '3', title: 'Weekly Goals Check', time: '10:00', enabled: false, weekly: true }
    ];
  });

  useEffect(() => {
    if ('Notification' in window) {
      setPermission(Notification.permission);
    }
  }, []);

  const registerServiceWorker = async () => {
    if ('serviceWorker' in navigator) {
      try {
        const registration = await navigator.serviceWorker.register('/sw.js', {
          scope: '/'
        });
        console.log('Service Worker registered successfully:', registration);

        // Wait for service worker to be ready
        await navigator.serviceWorker.ready;
        console.log('Service Worker is ready');

        return registration;
      } catch (error) {
        console.error('Service Worker registration failed:', error);
        return null;
      }
    }
    return null;
  };

  const isMobileDevice = () => {
    return /Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ||
           (navigator.maxTouchPoints && navigator.maxTouchPoints > 2);
  };

  const showNotificationSafely = async (title: string, options: NotificationOptions) => {
    try {
      const isMobile = isMobileDevice();
      console.log('Device type:', isMobile ? 'Mobile' : 'Desktop');
      console.log('Notification permission:', Notification.permission);

      // For mobile devices, always use ServiceWorker approach
      if (isMobile && 'serviceWorker' in navigator) {
        try {
          console.log('Mobile detected - using ServiceWorker approach...');
          let registration = await navigator.serviceWorker.getRegistration();
          if (!registration) {
            console.log('No existing registration, creating new one...');
            registration = await registerServiceWorker();
          }

          if (registration && registration.showNotification) {
            console.log('Showing notification via ServiceWorker (mobile optimized)');
            await registration.showNotification(title, {
              ...options,
              requireInteraction: true, // Keep notification visible longer on mobile
              badge: '/icon-192.png'
            });
            return true;
          } else {
            console.error('ServiceWorker registration failed or showNotification not available');
          }
        } catch (swError) {
          console.error('ServiceWorker approach failed:', swError);
        }
      }

      // Fallback to direct notification for desktop
      if ('Notification' in window && Notification.permission === 'granted') {
        try {
          console.log('Using direct notification (desktop)...');
          const notification = new Notification(title, options);

          notification.onclick = () => {
            console.log('Notification clicked - focusing window');
            window.focus();
            notification.close();
          };

          notification.onshow = () => {
            console.log('Notification shown successfully');
          };

          notification.onerror = (error) => {
            console.error('Direct notification error:', error);
          };

          return true;
        } catch (directError) {
          console.error('Direct notification failed:', directError);
        }
      }

      console.warn('All notification methods exhausted');
      return false;
    } catch (error) {
      console.error('Notification system error:', error);
      return false;
    }
  };

  const requestPermission = async () => {
    console.log('Requesting notification permission...');

    if ('Notification' in window) {
      try {
        // Check if permission is already granted to avoid multiple requests
        if (Notification.permission === 'granted') {
          setPermission('granted');
          showToast({
            type: 'success',
            title: 'Already Enabled!',
            message: 'Notifications are already working'
          });
          return;
        }

        const result = await Notification.requestPermission();
        console.log('Permission result:', result);
        setPermission(result);

        if (result === 'granted') {
          console.log('Permission granted, creating welcome notification...');

          // Use setTimeout and safe notification method
          setTimeout(async () => {
            const success = await showNotificationSafely('ðŸŽ¯ Hunter System Online!', {
              body: 'Notifications enabled! You\'ll receive quest reminders and updates.',
              icon: '/favicon.ico',
              tag: 'welcome-notification',
              silent: false
            });

            if (!success) {
              showToast({
                type: 'warning',
                title: 'Notification Setup',
                message: 'Notifications enabled but test failed. This is normal on some mobile browsers.'
              });
            }
          }, 200);

          showToast({
            type: 'success',
            title: 'Notifications Enabled!',
            message: 'You should see a welcome notification!'
          });

        } else if (result === 'denied') {
          showToast({
            type: 'warning',
            title: 'Notifications Denied',
            message: 'Please enable notifications in your browser settings to receive quest reminders'
          });
        } else {
          showToast({
            type: 'info',
            title: 'Permission Pending',
            message: 'Please respond to the notification permission prompt'
          });
        }
      } catch (error) {
        console.error('Error requesting notification permission:', error);
        showToast({
          type: 'error',
          title: 'Permission Error',
          message: 'Could not request notification permission. Please check your browser settings'
        });
      }
    } else {
      showToast({
        type: 'warning',
        title: 'Notifications Not Supported',
        message: 'Your browser does not support notifications'
      });
    }
  };

  const toggleReminder = (id: string) => {
    setReminders(prev => {
      const updated = prev.map(reminder => 
        reminder.id === id ? { ...reminder, enabled: !reminder.enabled } : reminder
      );
      localStorage.setItem('notification-reminders', JSON.stringify(updated));
      return updated;
    });
  };

  // Schedule notifications based on enabled reminders
  useEffect(() => {
    const scheduleNotifications = () => {
      reminders.forEach(reminder => {
        if (reminder.enabled && permission === 'granted') {
          const [hours, minutes] = reminder.time.split(':').map(Number);
          const now = new Date();
          const scheduledTime = new Date();
          scheduledTime.setHours(hours, minutes, 0, 0);

          // If time has passed today, schedule for tomorrow
          if (scheduledTime <= now) {
            scheduledTime.setDate(scheduledTime.getDate() + 1);
          }

          const timeUntilNotification = scheduledTime.getTime() - now.getTime();

          setTimeout(async () => {
            await showNotificationSafely(`Solo Leveling - ${reminder.title}`, {
              body: 'Time to level up your productivity!',
              icon: '/favicon.ico',
              badge: '/favicon.ico',
              tag: `reminder-${reminder.id}`
            });
          }, timeUntilNotification);
        }
      });
    };

    if (permission === 'granted') {
      scheduleNotifications();
    }
  }, [reminders, permission]);

  const testNotification = async () => {
    console.log('Test notification clicked, permission:', permission);

    if (permission === 'granted') {
      showToast({
        type: 'info',
        title: 'Sending Test...',
        message: 'Creating test notification...'
      });

      // Use setTimeout and safe notification method for mobile compatibility
      setTimeout(async () => {
        const success = await showNotificationSafely('ðŸ¹ Hunter System Test', {
          body: 'Test successful! Your quest reminders are working perfectly.',
          icon: '/favicon.ico',
          tag: 'test-notification',
          silent: false
        });

        if (success) {
          showToast({
            type: 'success',
            title: 'Test Notification Sent!',
            message: isMobileDevice() ? 'Check your notification panel!' : 'Check your notification area!'
          });
        } else {
          const isMobile = isMobileDevice();
          showToast({
            type: 'warning',
            title: 'Notification Issue',
            message: isMobile 
              ? 'Mobile notifications failed. Try: 1) Allow notifications in browser settings 2) Add this site to home screen 3) Try desktop mode'
              : 'Notifications may be blocked. Please allow notifications in your browser settings.'
          });

          // Additional debug info for mobile
          if (isMobile) {
            console.log('Mobile notification troubleshooting:');
            console.log('- User Agent:', navigator.userAgent);
            console.log('- Service Worker support:', 'serviceWorker' in navigator);
            console.log('- Notification support:', 'Notification' in window);
            console.log('- Permission:', Notification.permission);
            console.log('- Touch points:', navigator.maxTouchPoints);
          }
        }
      }, 100);

    } else if (permission === 'denied') {
      showToast({
        type: 'warning',
        title: 'Notifications Blocked',
        message: 'Please enable notifications in your browser settings first'
      });
    } else {
      showToast({
        type: 'info',
        title: 'Permission Required',
        message: 'Please grant notification permission first'
      });
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-[9999] flex items-center justify-center p-4">
      <div className="mystical-card max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-700">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-lg flex items-center justify-center">
              <Bell className="w-5 h-5 text-white" />
            </div>
            <h2 className="text-xl font-bold text-white font-['Orbitron']">
              NOTIFICATION SYSTEM
            </h2>
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-700 rounded-lg transition-colors"
          >
            <X className="w-5 h-5 text-gray-400" />
          </button>
        </div>

        {/* Permission Status */}
        <div className="p-6 border-b border-gray-700">
          <h3 className="text-lg font-semibold text-white mb-4">Permission Status</h3>

          <div className={`p-4 rounded-lg border ${
            permission === 'granted' ? 'bg-green-900/20 border-green-500/30' :
            permission === 'denied' ? 'bg-red-900/20 border-red-500/30' :
            'bg-yellow-900/20 border-yellow-500/30'
          }`}>
            <div className="flex items-center justify-between">
              <div>
                <p className={`font-semibold ${
                  permission === 'granted' ? 'text-green-400' :
                  permission === 'denied' ? 'text-red-400' :
                  'text-yellow-400'
                }`}>
                  {permission === 'granted' ? 'âœ… Notifications Enabled' :
                   permission === 'denied' ? 'âŒ Notifications Blocked' :
                   'â³ Permission Required'}
                </p>
                <p className="text-gray-300 text-sm mt-1">
                  {permission === 'granted' ? 'You will receive quest reminders and alerts' :
                   permission === 'denied' ? 'Go to browser settings > Site Settings > Notifications and allow for this site' :
                   'Click to enable browser notifications for reminders'}
                </p>
              </div>

              {permission !== 'granted' && (
                <button
                  onClick={requestPermission}
                  className="power-button ml-4"
                  disabled={permission === 'denied'}
                >
                  Enable
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Reminder Settings */}
        <div className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-white">Quest Reminders</h3>
            {permission === 'granted' && (
              <button
                onClick={testNotification}
                className="text-cyan-400 text-sm hover:text-cyan-300 transition-colors"
              >
                Test Notification
              </button>
            )}
          </div>

          <div className="space-y-4">
            {reminders.map(reminder => (
              <div 
                key={reminder.id}
                className="mystical-card p-4 flex items-center justify-between"
              >
                <div>
                  <h4 className="text-white font-semibold">{reminder.title}</h4>
                  <p className="text-gray-400 text-sm">
                    {reminder.weekly ? 'Weekly â€¢ ' : 'Daily â€¢ '}{reminder.time}
                  </p>
                </div>

                <button
                  onClick={() => toggleReminder(reminder.id)}
                  disabled={permission !== 'granted'}
                  className={`w-12 h-6 rounded-full transition-colors relative ${
                    reminder.enabled && permission === 'granted' 
                      ? 'bg-blue-500' 
                      : 'bg-gray-600'
                  } ${permission !== 'granted' ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  <div className={`w-5 h-5 rounded-full bg-white transition-transform absolute top-0.5 ${
                    reminder.enabled && permission === 'granted' 
                      ? 'translate-x-6' 
                      : 'translate-x-0.5'
                  }`} />
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Instructions */}
        <div className="p-6 border-t border-gray-700">
          <h3 className="text-lg font-semibold text-white mb-3">How It Works</h3>
          <div className="space-y-2 text-gray-300 text-sm">
            <p>â€¢ Notifications help you stay on track with your productivity goals</p>
            <p>â€¢ Daily reminders for quest briefings and evening reviews</p>
            <p>â€¢ Weekly check-ins to assess progress and adjust goals</p>
            <p>â€¢ All reminders respect your device's Do Not Disturb settings</p>
          </div>

          {permission === 'denied' && (
            <div className="mt-4 p-4 bg-red-900/20 border border-red-500/30 rounded-lg">
              <h4 className="text-red-400 font-semibold mb-2">How to Enable Notifications:</h4>
              <div className="text-gray-300 text-sm space-y-2">
                <p><strong>Chrome:</strong> Click the ðŸ”’ lock icon next to the URL â†’ Notifications â†’ Allow</p>
                <p><strong>Firefox:</strong> Click the shield icon â†’ Turn off blocking for Notifications</p>
                <p><strong>Safari:</strong> Safari menu â†’ Preferences â†’ Websites â†’ Notifications â†’ Allow</p>
                <p><strong>Edge:</strong> Click the ðŸ”’ icon â†’ Notifications â†’ Allow</p>
                <div className="mt-3 p-2 bg-blue-900/30 rounded border border-blue-500/30">
                  <p className="text-blue-300"><strong>Mobile:</strong> Notifications work best on desktop browsers. Mobile support varies by browser.</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

