import { useState, useEffect } from "react";
import { X, RefreshCw, Star } from "lucide-react";
import { getRandomQuote, getDailyQuote, type MotivationalQuote } from "@/data/motivationalQuotes";

interface MotivationalGreetingProps {
  userName?: string;
  onClose: () => void;
}

export function MotivationalGreeting({ userName = "Hunter", onClose }: MotivationalGreetingProps) {
  const [currentQuote, setCurrentQuote] = useState<MotivationalQuote>(() => getDailyQuote());
  const [greeting, setGreeting] = useState("");

  // Update quote daily - check if we need a new quote
  useEffect(() => {
    const checkForNewQuote = () => {
      const today = new Date().toDateString();
      const lastQuoteDate = localStorage.getItem('last-quote-date');

      if (lastQuoteDate !== today) {
        const newQuote = getDailyQuote();
        setCurrentQuote(newQuote);
        localStorage.setItem('last-quote-date', today);
      }
    };

    checkForNewQuote();

    // Check every hour if we need a new quote (in case user keeps app open overnight)
    const interval = setInterval(checkForNewQuote, 3600000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const hour = new Date().getHours();
    let timeGreeting = "";

    if (hour < 12) {
      timeGreeting = "Good morning";
    } else if (hour < 17) {
      timeGreeting = "Good afternoon";
    } else {
      timeGreeting = "Good evening";
    }

    const greetings = [
      `${timeGreeting}, ${userName}! Ready to level up today?`,
      `${timeGreeting}, ${userName}! Time to conquer your quests!`,
      `${timeGreeting}, ${userName}! Your adventure awaits!`,
      `${timeGreeting}, ${userName}! Let's rise stronger than yesterday!`,
      `${timeGreeting}, ${userName}! Today's battles will forge your legend!`,
      `${timeGreeting}, ${userName}! The Shadow Monarch believes in you!`,
      `${timeGreeting}, ${userName}! Every quest completed makes you stronger!`,
      `${timeGreeting}, ${userName}! Time to show the world your true power!`
    ];

    const randomGreeting = greetings[Math.floor(Math.random() * greetings.length)];
    setGreeting(randomGreeting);
  }, [userName]);

  const handleNewQuote = () => {
    setCurrentQuote(getRandomQuote());
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'perseverance': return 'text-red-400 bg-red-500/20 border-red-500/30';
      case 'strength': return 'text-orange-400 bg-orange-500/20 border-orange-500/30';
      case 'dreams': return 'text-blue-400 bg-blue-500/20 border-blue-500/30';
      case 'courage': return 'text-green-400 bg-green-500/20 border-green-500/30';
      case 'friendship': return 'text-purple-400 bg-purple-500/20 border-purple-500/30';
      case 'growth': return 'text-cyan-400 bg-cyan-500/20 border-cyan-500/30';
      default: return 'text-gray-400 bg-gray-500/20 border-gray-500/30';
    }
  };

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-2 sm:p-4">
      <div className="mystical-card max-w-2xl w-full max-h-[95vh] overflow-y-auto animate-in zoom-in-95 duration-300">
        <div className="p-3 sm:p-6 border-b border-gray-700">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2 sm:space-x-3">
              <div className="w-8 h-8 sm:w-10 sm:h-10 bg-gradient-to-r from-purple-500 to-blue-500 rounded-lg flex items-center justify-center">
                <Star className="w-4 h-4 sm:w-6 sm:h-6 text-white" />
              </div>
              <div>
                <h2 className="text-lg sm:text-xl font-bold text-white font-['Orbitron']">
                  DAILY MOTIVATION
                </h2>
                <p className="text-gray-400 text-xs sm:text-sm">Fuel your inner power</p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-gray-700 rounded-lg transition-colors flex-shrink-0"
            >
              <X className="w-5 h-5 text-gray-400" />
            </button>
          </div>
        </div>

        <div className="p-3 sm:p-6 space-y-4 sm:space-y-6">
          {/* Greeting */}
          <div className="text-center">
            <h3 className="text-lg sm:text-2xl font-bold text-white mb-2 font-['Orbitron'] leading-tight">
              {greeting}
            </h3>
            <p className="text-gray-300 text-sm sm:text-base">
              Another day, another chance to prove your strength!
            </p>
          </div>

          {/* Quote Section */}
          <div className="mystical-card p-3 sm:p-6 bg-gradient-to-br from-gray-900/60 to-gray-800/40">
            <div className="flex items-center justify-between mb-3 sm:mb-4">
              <div className={`px-2 sm:px-3 py-1 rounded-full border text-xs font-semibold uppercase tracking-wider ${getCategoryColor(currentQuote.category)}`}>
                {currentQuote.category}
              </div>
              <button
                onClick={handleNewQuote}
                className="p-2 hover:bg-gray-700 rounded-lg transition-colors group"
                title="Get new quote"
              >
                <RefreshCw className="w-4 h-4 text-gray-400 group-hover:text-white transition-colors" />
              </button>
            </div>

            <blockquote className="text-white text-sm sm:text-lg leading-relaxed mb-3 sm:mb-4 font-medium">
              "{currentQuote.text}"
            </blockquote>

            <div className="text-right">
              <p className="text-cyan-400 font-semibold text-sm sm:text-base">â€” {currentQuote.author}</p>
              <p className="text-gray-400 text-xs sm:text-sm">{currentQuote.anime}</p>
            </div>
          </div>

          {/* Action Section */}
          <div className="text-center space-y-3 sm:space-y-4">
            <p className="text-gray-300 text-sm sm:text-base">
              Every completed quest makes you stronger. Your Hunter journey starts now!
            </p>

            <div className="flex flex-col md:flex-row gap-3">
              <button
                onClick={handleNewQuote}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-semibold transition-colors flex items-center justify-center space-x-2"
              >
                <RefreshCw className="w-5 h-5" />
                <span>New Quote</span>
              </button>
              <button
                onClick={onClose}
                className="flex-1 power-button"
              >
                Start My Journey
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/Toast.tsx.content:
import { useState, useEffect } from "react";
import { X, CheckCircle, AlertTriangle, Info, XCircle } from "lucide-react";

export type ToastType = 'success' | 'error' | 'warning' | 'info';

interface Toast {
  id: string;
  type: ToastType;
  title: string;
  message?: string;
  duration?: number;
}

interface ToastProps {
  toast: Toast;
  onClose: (id: string) => void;
}

const toastIcons = {
  success: CheckCircle,
  error: XCircle,
  warning: AlertTriangle,
  info: Info
};

const toastColors = {
  success: 'from-green-500/80 to-emerald-500/80 border-green-500 text-green-100',
  error: 'from-red-500/80 to-pink-500/80 border-red-500 text-red-100',
  warning: 'from-yellow-500/80 to-orange-500/80 border-yellow-500 text-yellow-100',
  info: 'from-cyan-500/80 to-blue-500/80 border-cyan-500 text-cyan-100'
};

function ToastItem({ toast, onClose }: ToastProps) {
  const Icon = toastIcons[toast.type];

  useEffect(() => {
    const timer = setTimeout(() => {
      onClose(toast.id);
    }, toast.duration || 4000);

    return () => clearTimeout(timer);
  }, [toast.id, toast.duration, onClose]);

  return (
    <div className={`bg-gradient-to-r ${toastColors[toast.type]} p-4 mb-3 shadow-xl border-2 rounded-lg backdrop-blur-md animate-in slide-in-from-right-full`}>
      <div className="flex items-start space-x-3">
        <Icon className="w-5 h-5 mt-0.5 flex-shrink-0" />
        <div className="flex-1">
          <h4 className="font-bold text-white">{toast.title}</h4>
          {toast.message && (
            <p className="text-sm text-white/90 mt-1">{toast.message}</p>
          )}
        </div>
        <button
          onClick={() => onClose(toast.id)}
          className="text-white/70 hover:text-white transition-colors"
        >
          <X className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
}

let toastCallbacks: Array<(toast: Omit<Toast, 'id'>) => void> = [];

export function showToast(toast: Omit<Toast, 'id'>) {
  toastCallbacks.forEach(callback => callback(toast));
}

export function ToastContainer() {
  const [toasts, setToasts] = useState<Toast[]>([]);

  useEffect(() => {
    const callback = (toast: Omit<Toast, 'id'>) => {
      const newToast: Toast = {
        ...toast,
        id: Date.now().toString() + Math.random().toString(36).substr(2, 9)
      };
      setToasts(prev => [...prev, newToast]);
    };

    toastCallbacks.push(callback);

    return () => {
      toastCallbacks = toastCallbacks.filter(cb => cb !== callback);
    };
  }, []);

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  if (toasts.length === 0) return null;

  return (
    <div className="fixed top-4 right-4 z-50 max-w-sm w-full">
      {toasts.map(toast => (
        <ToastItem key={toast.id} toast={toast} onClose={removeToast} />
      ))}
    </div>
  );
}

