import { useState, useEffect } from "react";
import { ChevronLeft, ChevronRight, Plus, Calendar as CalendarIcon } from "lucide-react";
import { showToast } from "@/components/ui/toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";

interface CalendarEvent {
  id: string;
  title: string;
  date: string;
  type: 'main-mission' | 'training' | 'side-quest';
  completed: boolean;
}

interface WeeklyViewProps {
  goals?: Array<{
    id: string;
    title: string;
    dueDate?: string;
    completed: boolean;
    category: string;
  }>;
}

export function WeeklyView({ goals = [] }: WeeklyViewProps) {
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const today = new Date();
    const dayOfWeek = today.getDay();
    const diff = today.getDate() - dayOfWeek;
    return new Date(today.setDate(diff));
  });

  const [isAddingEvent, setIsAddingEvent] = useState(false);
  const [newEventTitle, setNewEventTitle] = useState('');
  const [newEventType, setNewEventType] = useState<'main-mission' | 'training' | 'side-quest'>('main-mission');
  const [newEventDate, setNewEventDate] = useState(() => {
    const today = new Date();
    return `${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, '0')}-${String(today.getDate()).padStart(2, '0')}`;
  });

  const queryClient = useQueryClient();

  // Fetch events from database
  const { data: calendarEvents = [], isLoading } = useQuery({
    queryKey: ['/api/calendar-events'],
    queryFn: async () => {
      const response = await fetch('/api/calendar-events');
      if (!response.ok) throw new Error('Failed to fetch events');
      return response.json();
    }
  });

  // Create event mutation
  const createEventMutation = useMutation({
    mutationFn: async (eventData: any) => {
      const response = await fetch('/api/calendar-events', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(eventData)
      });
      if (!response.ok) throw new Error('Failed to create event');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/calendar-events'] });
    }
  });

  // Update event mutation
  const updateEventMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/calendar-events/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Failed to update event');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/calendar-events'] });
    }
  });

  // Delete event mutation
  const deleteEventMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/calendar-events/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Failed to delete event');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/calendar-events'] });
    }
  });

  const getWeekDates = (startDate: Date) => {
    const dates = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startDate);
      date.setDate(startDate.getDate() + i);
      dates.push(date);
    }
    return dates;
  };

  const navigateWeek = (direction: 'prev' | 'next') => {
    const newStart = new Date(currentWeekStart);
    newStart.setDate(currentWeekStart.getDate() + (direction === 'next' ? 7 : -7));
    setCurrentWeekStart(newStart);
  };

  const getEventsForDate = (date: Date) => {
    const dateStr = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`;

    // Combine calendar events and goals with due dates
    const events = [...calendarEvents];

    goals.forEach(goal => {
      if (goal.dueDate === dateStr) {
        events.push({
          id: `goal-${goal.id}`,
          title: goal.title,
          date: dateStr,
          type: goal.category as 'main-mission' | 'training' | 'side-quest' || 'main-mission',
          completed: goal.completed
        });
      }
    });

    return events.filter(event => event.date === dateStr);
  };

  const getEventTypeColor = (type: string) => {
    switch (type) {
      case 'main-mission': return 'bg-red-500/20 text-red-400 border-red-500/30';
      case 'training': return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
      case 'side-quest': return 'bg-green-500/20 text-green-400 border-green-500/30';
      default: return 'bg-gray-500/20 text-gray-400 border-gray-500/30';
    }
  };

  const handleAddEvent = () => {
    if (!newEventTitle.trim()) return;

    createEventMutation.mutate({
      title: newEventTitle,
      date: newEventDate,
      type: newEventType,
      completed: false
    }, {
      onSuccess: () => {
        setNewEventTitle('');
        setIsAddingEvent(false);
        const today = new Date();
        setNewEventDate(`${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, '0')}-${String(today.getDate()).padStart(2, '0')}`);

        showToast({
          type: 'success',
          title: 'Quest Event Added!',
          message: 'Event added to your weekly schedule'
        });
      },
      onError: () => {
        showToast({
          type: 'error',
          title: 'Failed to Add Event',
          message: 'Please try again'
        });
      }
    });
  };

  const handleToggleEventComplete = (eventId: string) => {
    const event = calendarEvents.find((e: CalendarEvent) => e.id === eventId);
    if (!event) return;

    updateEventMutation.mutate({
      id: eventId,
      data: { completed: !event.completed }
    }, {
      onSuccess: () => {
        showToast({
          type: 'success',
          title: !event.completed ? 'Quest Completed!' : 'Quest Reopened',
          message: `"${event.title}" marked as ${!event.completed ? 'completed' : 'pending'}`
        });
      }
    });
  };

  const handleDeleteEvent = (eventId: string) => {
    const event = calendarEvents.find((e: CalendarEvent) => e.id === eventId);
    if (!event) return;

    deleteEventMutation.mutate(eventId, {
      onSuccess: () => {
        showToast({
          type: 'success',
          title: 'Event Deleted',
          message: `"${event.title}" has been removed`
        });
      }
    });
  };

  const weekDates = getWeekDates(currentWeekStart);
  const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const today = new Date();

  return (
    <div className="w-full space-y-4">
      {/* Weekly Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl sm:text-2xl font-bold text-white font-['Orbitron']">
            <span className="bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
              WEEKLY MISSIONS
            </span>
          </h2>
          <p className="text-gray-400 text-sm">Your 7-day quest overview</p>
        </div>
        <button 
          className="power-button text-sm px-3 py-2"
          onClick={() => setIsAddingEvent(true)}
        >
          <Plus className="w-4 h-4 mr-1" />
          Add
        </button>
      </div>

      {/* Week Navigation - Mobile Optimized */}
      <div className="mystical-card p-2 md:p-4">
        <div className="flex items-center justify-between mb-2 md:mb-4">
          <button 
            onClick={() => navigateWeek('prev')}
            className="p-2 md:p-2 hover:bg-gray-700 rounded-lg transition-colors touch-manipulation min-h-[44px] min-w-[44px] flex items-center justify-center"
            data-testid="button-prev-week"
          >
            <ChevronLeft className="w-5 h-5 text-cyan-400" />
          </button>

          <div className="text-center flex-1 px-2">
            <h3 className="text-sm md:text-lg font-bold text-white font-['Orbitron'] leading-tight">
              {weekDates[0].toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
            </h3>
            <p className="text-gray-400 text-xs md:text-sm leading-tight mt-1">
              {weekDates[0].toLocaleDateString()} - {weekDates[6].toLocaleDateString()}
            </p>
          </div>

          <button 
            onClick={() => navigateWeek('next')}
            className="p-2 md:p-2 hover:bg-gray-700 rounded-lg transition-colors touch-manipulation min-h-[44px] min-w-[44px] flex items-center justify-center"
            data-testid="button-next-week"
          >
            <ChevronRight className="w-5 h-5 text-cyan-400" />
          </button>
        </div>

        {/* Innovative Mobile-First Week Grid */}
        <div className="grid grid-cols-7 gap-px md:gap-2 bg-gray-800/20 p-px md:p-0 rounded-lg md:rounded-none">
          {weekDates.map((date, index) => {
            const isToday = date.toDateString() === today.toDateString();
            const dayEvents = getEventsForDate(date);

            return (
              <div
                key={date.toISOString()}
                className={`
                  bg-gray-900/80 md:bg-transparent
                  p-1.5 md:p-3 rounded-md md:rounded-lg 
                  min-h-[90px] md:min-h-[120px] 
                  transition-all duration-200 
                  flex flex-col
                  ${isToday 
                    ? 'bg-gradient-to-br from-cyan-500/30 to-blue-500/20 border-2 border-cyan-400/60 shadow-lg shadow-cyan-500/10' 
                    : 'hover:bg-white/5 md:border md:border-gray-700/30 hover:border-gray-600/50'
                  }
                `}
              >
                {/* Day Header - Improved */}
                <div className="text-center mb-2 flex-shrink-0">
                  <div className="text-[10px] md:text-xs text-gray-400 font-semibold uppercase tracking-wide">
                    {dayNames[index]}
                  </div>
                  <div className={`
                    text-sm md:text-base font-bold mt-0.5
                    ${isToday 
                      ? 'text-cyan-300 bg-cyan-500/20 rounded-full w-6 h-6 md:w-7 md:h-7 flex items-center justify-center mx-auto' 
                      : 'text-white'
                    }
                  `}>
                    {date.getDate()}
                  </div>
                </div>

                {/* Events - Better Mobile Layout */}
                <div className="space-y-1 flex-1 overflow-hidden">
                  {dayEvents.slice(0, 3).map(event => (
                    <div
                      key={event.id}
                      className={`
                        text-[9px] md:text-xs px-1.5 py-1 rounded-md
                        ${getEventTypeColor(event.type)} 
                        ${event.completed ? 'opacity-60 line-through' : ''} 
                        cursor-pointer transition-all duration-150
                        hover:scale-105 active:scale-95
                        border border-white/10
                        backdrop-blur-sm
                      `}
                      onClick={() => !event.id.startsWith('goal-') && handleToggleEventComplete(event.id)}
                      title={event.title}
                    >
                      <div className="truncate leading-tight">
                        {event.title.length > 8 ? event.title.substring(0, 8) + '...' : event.title}
                      </div>
                    </div>
                  ))}
                  {dayEvents.length > 3 && (
                    <div className="text-[8px] md:text-xs text-gray-400 text-center py-1 bg-gray-800/30 rounded-md">
                      +{dayEvents.length - 3} more
                    </div>
                  )}
                  {dayEvents.length === 0 && (
                    <div className="text-[8px] md:text-xs text-gray-600 text-center py-2 border border-dashed border-gray-700/30 rounded-md">
                      No events
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Add Event Modal */}
      {isAddingEvent && (
        <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4">
          <div className="mystical-card max-w-md w-full">
            <div className="p-4 border-b border-gray-700">
              <h3 className="text-lg font-bold text-white font-['Orbitron']">Add Quest Event</h3>
            </div>

            <div className="p-4 space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Quest Title
                </label>
                <input
                  type="text"
                  value={newEventTitle}
                  onChange={(e) => setNewEventTitle(e.target.value)}
                  className="w-full bg-gray-800 border border-gray-600 rounded-lg px-3 py-2 text-white focus:border-cyan-500 focus:outline-none"
                  placeholder="Enter quest title..."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Date
                </label>
                <input
                  type="date"
                  value={newEventDate}
                  onChange={(e) => setNewEventDate(e.target.value)}
                  className="w-full bg-gray-800 border border-gray-600 rounded-lg px-3 py-2 text-white focus:border-cyan-500 focus:outline-none"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Quest Type
                </label>
                <select
                  value={newEventType}
                  onChange={(e) => setNewEventType(e.target.value as any)}
                  className="w-full bg-gray-800 border border-gray-600 rounded-lg px-3 py-2 text-white focus:border-cyan-500 focus:outline-none"
                >
                  <option value="main-mission">Main Mission</option>
                  <option value="training">Training</option>
                  <option value="side-quest">Side Quest</option>
                </select>
              </div>

              <div className="flex space-x-3 pt-4">
                <button
                  onClick={() => setIsAddingEvent(false)}
                  className="flex-1 bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg font-semibold transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleAddEvent}
                  className="flex-1 power-button"
                  disabled={!newEventTitle.trim()}
                >
                  Add Quest
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

public/sw.js.content:
// Solo Leveling Productivity App - Service Worker
const CACHE_NAME = 'solo-hunter-v1.2.0';
const OFFLINE_URL = '/offline.html';

// Assets to cache for offline functionality
const CACHE_URLS = [
  '/',
  '/dashboard',
  '/settings',
  '/calendar',
  '/analytics',
  '/notes',
  '/streaks',
  '/offline.html',
  '/manifest.json',
  '/favicon.ico',
  '/icon-192x192.png',
  '/icon-512x512.png'
];

// Install event - cache critical resources
self.addEventListener('install', (event) => {
  console.log('Service Worker: Installing...');

  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log('Service Worker: Caching critical resources');
        // Cache resources individually to avoid failures
        const cachePromises = CACHE_URLS.map(url => {
          return cache.add(url).catch(err => {
            console.warn(`Failed to cache ${url}:`, err);
            return Promise.resolve(); // Continue with other resources
          });
        });
        return Promise.all(cachePromises);
      })
      .then(() => {
        console.log('Service Worker: Installation complete');
        self.skipWaiting();
      })
      .catch((error) => {
        console.error('Service Worker: Cache installation failed', error);
      })
  );
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
  console.log('Service Worker: Activating...');

  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('Service Worker: Deleting old cache', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => {
      console.log('Service Worker: Activated');
      return self.clients.claim();
    })
  );
});

// Fetch event - serve cached content when offline
self.addEventListener('fetch', (event) => {
  // Skip non-GET requests
  if (event.request.method !== 'GET') return;

  // Skip chrome-extension and other non-http requests
  if (!event.request.url.startsWith('http')) return;

  event.respondWith(
    caches.match(event.request)
      .then((cachedResponse) => {
        // Return cached version if available
        if (cachedResponse) {
          return cachedResponse;
        }

        // Otherwise, fetch from network
        return fetch(event.request)
          .then((response) => {
            // Don't cache non-successful responses
            if (!response || response.status !== 200 || response.type !== 'basic') {
              return response;
            }

            // Clone response for caching
            const responseToCache = response.clone();

            caches.open(CACHE_NAME)
              .then((cache) => {
                cache.put(event.request, responseToCache);
              });

            return response;
          })
          .catch(() => {
            // Return offline page for navigation requests
            if (event.request.mode === 'navigate') {
              return caches.match(OFFLINE_URL);
            }
          });
      })
  );
});

// Push notification event
self.addEventListener('push', (event) => {
  console.log('Service Worker: Push notification received');

  const options = {
    body: event.data ? event.data.text() : 'New quest awaits, Hunter!',
    icon: '/icons/icon-192x192.png',
    badge: '/icons/badge-72x72.png',
    vibrate: [200, 100, 200],
    data: {
      dateOfArrival: Date.now(),
      primaryKey: '1'
    },
    actions: [
      {
        action: 'view',
        title: 'View Quest',
        icon: '/icons/action-view.png'
      },
      {
        action: 'dismiss',
        title: 'Dismiss',
        icon: '/icons/action-dismiss.png'
      }
    ],
    tag: 'hunter-notification',
    requireInteraction: true
  };

  event.waitUntil(
    self.registration.showNotification('Solo Hunter Alert', options)
  );
});

// Notification click event
self.addEventListener('notificationclick', (event) => {
  console.log('Service Worker: Notification clicked');

  event.notification.close();

  if (event.action === 'view') {
    // Open the app
    event.waitUntil(
      clients.openWindow('/')
    );
  } else if (event.action === 'dismiss') {
    // Just close the notification
    return;
  } else {
    // Default action - open app
    event.waitUntil(
      clients.openWindow('/')
    );
  }
});

// Background sync for offline actions
self.addEventListener('sync', (event) => {
  console.log('Service Worker: Background sync triggered');

  if (event.tag === 'background-sync') {
    event.waitUntil(
      // Sync any pending data when online
      syncPendingData()
    );
  }
});

// Periodic background sync for notifications
self.addEventListener('periodicsync', (event) => {
  if (event.tag === 'hunter-reminders') {
    event.waitUntil(
      checkAndSendReminders()
    );
  }
});

// Helper function to sync pending data
async function syncPendingData() {
  try {
    // Get pending data from IndexedDB or localStorage
    const pendingActions = await getPendingActions();

    for (const action of pendingActions) {
      try {
        await fetch(action.url, {
          method: action.method,
          headers: action.headers,
          body: action.body
        });

        // Remove from pending list on success
        await removePendingAction(action.id);
      } catch (error) {
        console.error('Failed to sync action:', error);
      }
    }
  } catch (error) {
    console.error('Background sync failed:', error);
  }
}

// Helper function to check and send reminders
async function checkAndSendReminders() {
  try {
    // Check if it's time for daily reminders
    const now = new Date();
    const reminderTime = 9; // 9 AM

    if (now.getHours() === reminderTime) {
      await self.registration.showNotification('Daily Hunter Check-in', {
        body: 'Time to review your quests and plan your day!',
        icon: '/icons/icon-192x192.png',
        badge: '/icons/badge-72x72.png',
        vibrate: [200, 100, 200],
        tag: 'daily-reminder',
        actions: [
          {
            action: 'open-app',
            title: 'Open App'
          }
        ]
      });
    }
  } catch (error) {
    console.error('Reminder check failed:', error);
  }
}

// Helper functions for pending actions
async function getPendingActions() {
  // Implementation would use IndexedDB
  return [];
}

async function removePendingAction(id) {
  // Implementation would use IndexedDB
  return true;
}

// Message handling for communication with main thread
self.addEventListener('message', (event) => {
  console.log('Service Worker: Message received', event.data);

  if (event.data && event.data.type) {
    switch (event.data.type) {
      case 'SKIP_WAITING':
        self.skipWaiting();
        break;

      case 'GET_VERSION':
        event.ports[0].postMessage({ version: CACHE_NAME });
        break;

      case 'CACHE_URLS':
        event.waitUntil(
          caches.open(CACHE_NAME)
            .then(cache => cache.addAll(event.data.urls))
        );
        break;

      case 'SCHEDULE_NOTIFICATION':
        scheduleNotification(event.data.notification);
        break;

      default:
        console.log('Unknown message type:', event.data.type);
    }
  }
});

// Schedule notification function
function scheduleNotification(notificationData) {
  const { title, body, delay = 0, tag = 'scheduled' } = notificationData;

  setTimeout(() => {
    self.registration.showNotification(title, {
      body,
      icon: '/icons/icon-192x192.png',
      badge: '/icons/badge-72x72.png',
      vibrate: [200, 100, 200],
      tag,
      requireInteraction: true
    });
  }, delay);
}

